 namespace Wfdg1; use FnCei; use Exception; class SanitizeTest extends BaseTestCase { public function testWhenWhitelistedFieldDoesntExistItContinuesSanitizingNextFields() { $mncio = $this->gump->sanitize(["\x77\x68\x69\x74\x65\x6c\x69\x73\x74\145\144\x5f\x66\x69\x65\154\144" => "\x74\x65\x78\164\15"], ["\x6e\157\x6e\137\x65\170\x69\x73\x74\x65\x6e\164", "\167\x68\x69\x74\x65\154\151\x73\x74\x65\144\137\146\151\145\x6c\x64"]); $this->assertEquals(["\x77\150\151\x74\145\x6c\x69\163\164\145\x64\x5f\146\151\145\154\144" => "\164\x65\170\164"], $mncio); } public function testArrayValuesAreAlsoSanitizedIfInputIsArray() { $mncio = $this->gump->sanitize(["\x77\x68\x69\x74\145\x6c\x69\x73\x74\x65\x64\137\x66\x69\x65\x6c\144" => ["\x6b\145\171" => "\x74\x65\170\x74\xd"]]); $this->assertEquals(["\167\x68\x69\164\x65\154\151\x73\x74\145\144\137\146\151\145\x6c\144" => ["\x6b\x65\x79" => "\x74\145\x78\x74"]], $mncio); } public function testWhenWhitelistedFieldsArePresentThenOnlyTheyAreReturnedAndSanitized() { $mncio = $this->gump->sanitize(["\x77\x68\151\164\x65\x6c\x69\x73\164\145\x64\x5f\146\151\145\x6c\x64" => "\x74\145\x78\x74\xd", "\164\x65\x73\x74\62" => "\xc5\275\154\165\305\245\157\165\304\x8d\153\xc3\275\x20\x6b\xc5\257\xc5\x88"], ["\167\150\151\x74\145\154\x69\x73\164\145\144\137\146\x69\145\154\144"]); $this->assertEquals(["\167\x68\151\x74\x65\154\x69\x73\x74\145\144\137\146\151\x65\x6c\144" => "\x74\145\x78\x74"], $mncio); } }
